0,0,0,0,0,0,0,0)
  val res = Solution.threeSum(a)
  println(res)

  object Solution {
    def threeSum(nums: Array[Int]): List[List[Int]] = {
      def twoSum(startingIndex: Int, remainingSum: Int) = {
        @scala.annotation.tailrec
        def find(index: Int, map: Map[Int, Int], twoSums: List[List[Int]]): List[List[Int]] = {
          if(index == nums.length) {
            twoSums
          } else {
            map.get(remainingSum - nums(index)) match {
              case Some(i) => find(index + 1, map + (nums(index) -> index), prepList(nums(i), nums(index)) +: twoSums)
              case None => find(index + 1, map + (nums(index) -> index), twoSums)
            }
          }
        }

        def prepList(num1: Int, num2: Int): List[Int] = {
          if(num1 < num2) List(num1, num2) else List(num2, num1)
        }
        find(startingIndex, Map(), Nil)
      }

      @scala.annotation.tailrec
      def findThreeSum(index: Int, threeSums: List[List[Int]]): List[List[Int]] = {
        if(index == nums.length) {
          threeSums
        } else {
          if (index == 0 || nums(index) != nums(index - 1)) {
            twoSum(index + 1, -nums(index)) match {
              case Nil => findThreeSum(index + 1, threeSums)
              case x => findThreeSum(index + 1, x.map(insertInSortedList(nums(index), _)) ++ threeSums)
            }
          } else {
            findThreeSum(index + 1, threeSums)
          }

        }
      }

      def insertInSortedList(element: Int, sortedElements: List[Int]): List[Int] = {
        val (p1, p2) = sortedElements.partition(_ < element)
        p1 ++ Seq(element) ++ p2
      }

      findThreeSum(0, Nil).distinct
    }
  }
}
