  //  val input = Array(7, 6, 9, 6, 9, 6, 9, 7)
  val res = Solution.minJumps(input)
  println(res)

  object Solution {
    def minJumps(arr: Array[Int]): Int = {

      lazy val minJumpCount = Array.fill[Int](arr.length)(-1)

      @scala.annotation.tailrec
      def mapIndices(index: Int = 0, map: Map[Int, Set[Int]] = Map()): Map[Int, Set[Int]] = {
        if (index == arr.length) {
          map
        } else {
          map.get(arr(index)) match {
            case None =>
              mapIndices(index + 1, map + (arr(index) -> Set(index)))
            case Some(indices) =>
              mapIndices(index + 1, map + (arr(index) -> (indices + index)))
          }
        }
      }

      var numIndices: Map[Int, Set[Int]] = mapIndices()

      def neighboursOf(index: Int, visitedNeighbours: Set[Int]): Set[Int] = {
        val sameNumNeighbours = numIndices(arr(index))
        numIndices = numIndices + (arr(index) -> Set())
        if (index == 0) {
          sameNumNeighbours + (index + 1) - index
        } else if (index == arr.length - 1) {
          sameNumNeighbours + (index - 1) - index
        } else {
          sameNumNeighbours + (index - 1) + (index + 1) - index
        } -- visitedNeighbours
      }

      @scala.annotation.tailrec
      def fillMinJumps(jumpCount: Int, neighbours: Set[Int]): Unit = {
        val updatedNeighbours: Set[Int] =
          neighbours.collect {
            case neighbour if minJumpCount(neighbour) == -1 =>
              minJumpCount(neighbour) = jumpCount + 1
              neighbour
          }
        if(updatedNeighbours.contains(0)) {
          ()
        } else {
          val allNextLevelNeighbours: Set[Int] = updatedNeighbours.flatMap(neighboursOf(_, updatedNeighbours))
          fillMinJumps(jumpCount + 1, allNextLevelNeighbours)
        }
      }

      if (arr.length == 1) {
        0
      } else {
        minJumpCount(arr.length - 1) = 0
//        println(numIndices)
        fillMinJumps(0, neighboursOf(arr.length - 1, Set()))
//        println(minJumpCount.mkString(","))
        minJumpCount(0)
      }
    }
  }

}
