package com.algo.dp

object LongestDuplicatedSubstring2 extends App {

  val s = ""
//  val s = "abcd"
  val res = Solution.longestDupSubstring(s)
  println(res)

  object Solution {

    def longestDupSubstring(S: String): String = {

      object SuffixOrder extends Ordering[Int] {
        override def compare(x: Int, y: Int): Int = {
          S.substring(x) compare S.substring(y)
        }
      }

      @scala.annotation.tailrec
      def allSuffixes(index: Int, suffixes: Array[Int]): Array[Int] = {
        if(index < 0) {
          suffixes
        } else {
          suffixes(index) = index
          allSuffixes(index - 1, suffixes)
        }
      }

      def prepSuffixArray(): Array[Int] = {
        val suffixes = allSuffixes(S.length - 1, new Array[Int](S.length))
        suffixes.sorted(SuffixOrder)
      }

      def prepLongestCommonPrefixArray(suffixArray: Array[Int]): Array[(Int, Int)] = {
        val lcp: Array[(Int, Int)] = new Array[(Int, Int)](suffixArray.length)
        @scala.annotation.tailrec
        def prep(index: Int): Unit = {
          if(index == suffixArray.length) {
            ()
          } else if (index == 0) {
            lcp(index) = (0, 0)
            prep(index + 1)
          } else {
            lcp(index) = (findLcpLen(suffixArray(index - 1), suffixArray(index)), index)
            prep(index + 1)
          }
        }
        prep(0)
        lcp
      }

      def findLcpLen(suffix1Index: Int, suffix2Index: Int): Int = {
        @scala.annotation.tailrec
        def find(currS1Index: Int, currS2Index: Int, len: Int): Int = {
          if(currS1Index == S.length || currS2Index == S.length) {
            len
          } else if (S(currS1Index) == S(currS2Index)) {
            find(currS1Index + 1, currS2Index + 1, len + 1)
          } else {
            len
          }
        }
        find(suffix1Index, suffix2Index, 0)
      }

      val suffixArray = prepSuffixArray()
      val lcpArray = prepLongestCommonPrefixArray(suffixArray)
//      println(suffixArray.mkString(","))
//      println(lcpArray.mkString(","))
      val (maxLen, maxLenIndex) = lcpArray.maxBy(_._1)
      if(maxLen == 0) {
        ""
      } else {
        S.substring(suffixArray(maxLenIndex), suffixArray(maxLenIndex) + maxLen)
      }
    }
  }
}
