package com.algo.dp

object LongestDuplicatedSubstringBinSearch extends App {

  val s = ""
//  val s = "banana"
  val res = Solution.longestDupSubstring(s)
  println(res)

  object Solution {
    def longestDupSubstring(S: String): String = {
      val suffixes = new Array[String](S.length)
      @scala.annotation.tailrec
      def allSuffixes(index: Int, prevSuffix: String): Unit = {
        if(index < 0) {
          ()
        } else {
          val suffix = s"${S(index)}$prevSuffix"
          suffixes(index) = suffix
          allSuffixes(index - 1, suffix)
        }
      }

      def prepSuffixArray(): Array[String] = {
        allSuffixes(S.length - 1, "")
        suffixes.sorted
      }

      def prepLongestCommonPrefixArray(suffixArray: Array[String]): Array[(Int, Int)] = {
        val lcp: Array[(Int, Int)] = new Array[(Int, Int)](suffixArray.length)
        @scala.annotation.tailrec
        def prep(index: Int): Unit = {
          if(index == suffixArray.length) {
            ()
          } else if (index == 0) {
            lcp(index) = (0, 0)
            prep(index + 1)
          } else {
            lcp(index) = (findLcpLenBS(suffixArray(index - 1), suffixArray(index)), index)
            prep(index + 1)
          }
        }
        prep(0)
        lcp
      }

      def findLcpLen(str1: String, str2: String): Int = {
        @scala.annotation.tailrec
        def find(index: Int, len: Int): Int = {
          if(index == str1.length || index == str2.length) {
            len
          } else if (str1(index) == str2(index)) {
            find(index + 1, len + 1)
          } else {
            len
          }
        }
        find(0, 0)
      }

      def findLcpLenBS(str1: String, str2: String): Int = {
        val (small, large) = if(str1.length < str2.length) (str1, str2) else (str2, str1)
        @scala.annotation.tailrec
        def find(low: Int, high: Int, currPrefix: String): Int = {
          if(high < low) {
            currPrefix.length
          } else {
            val mid = (low + high) / 2
            val smallSubStr = small.substring(low, mid + 1)
            if(smallSubStr == large.substring(low, mid + 1)) {
              find(mid + 1, high, s"$currPrefix$smallSubStr")
            } else {
              find(low, mid - 1, currPrefix)
            }
          }
        }
        find(0, small.length - 1, "")
      }

      val suffixArray = prepSuffixArray()
      val lcpArray = prepLongestCommonPrefixArray(suffixArray)
//      println(suffixArray.mkString(","))
//      println(lcpArray.mkString(","))
      val (maxLen, maxLenIndex) = lcpArray.maxBy(_._1)
      if(maxLen == 0) {
        ""
      } else {
        suffixArray(maxLenIndex).substring(0, maxLen)
      }
    }
  }
}
